import React from 'react'
import images from '../Helpers/Images'
import { useState } from 'react'
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAngleLeft, faAngleRight } from '@fortawesome/free-solid-svg-icons'

function Carousel() {
  let [currImg, setImg] = useState(0);

  const backgroundStyle = {
    background: `url(${images[currImg].img})`, backgroundSize: 'cover', height: '100%' 
  }
  const carStyle = {
   height: '300px'
  }
  return (
    <div className='h-85' style={carStyle} >
      <div className='h-85 flex backgroundStyle' style={{background: `url(${images[currImg].img})`}}>
        <div className='flex flex-5p justify-center items-center h-full bg-black bg-opacity-40'  
          onClick={()=> {currImg == 0 ? setImg(2): setImg(currImg- 1) ; console.log(currImg)}}>
          <FontAwesomeIcon icon={faAngleLeft} size='2x' color='white'/>
        </div>
        <div className='flex flex-90p  h-full'></div>
        <div className='flex flex-5p justify-center items-center h-full bg-black bg-opacity-40'
          onClick={()=> {currImg == 2 ? setImg(0) : setImg(currImg + 1) }}>
          <FontAwesomeIcon icon={faAngleRight} size='2x' color='white'/>
        </div>

      </div>
    </div>
  )
}

export default Carousel
